  const httpServer = createServer(app);

  // WebSocket server setup for real-time chat on a separate port to avoid Vite conflicts
  const wss = new WebSocketServer({ 
    server: httpServer,
    path: '/ws'
  });

  wss.on('connection', (ws: any, req: any) => {
    console.log('New WebSocket connection');

    ws.on('message', async (data: any) => {
      try {
        const message = JSON.parse(data.toString());

        if (message.type === 'authenticate') {
          // Authenticate user and store connection
          const { userId } = message;
          if (userId) {
            wsConnections.set(userId, ws);
            ws.userId = userId;
            ws.send(JSON.stringify({ type: 'authenticated', userId }));
            console.log(`User ${userId} authenticated via WebSocket`);
          }
        }

        if (message.type === 'chat_message') {
          const { fromUserId, toUserId, content, messageType } = message;

          // Save message to database
          const savedMessage = await storage.createMessage({
            fromUserId,
            toUserId, 
            content,
            isRead: false
          });

          // Broadcast to recipient
          const responseMessage = {
            type: 'new_message',
            id: savedMessage.id,
            fromUserId,
            toUserId,
            content: savedMessage.content,
            createdAt: savedMessage.createdAt,
            isRead: false
          };

          // Send to recipient
          broadcastToUser(toUserId, responseMessage);

          // Confirm to sender  
          ws.send(JSON.stringify({ 
            type: 'message_sent', 
            messageId: savedMessage.id,
            originalMessage: responseMessage 
          }));
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
        ws.send(JSON.stringify({ type: 'error', message: 'Invalid message format' }));
      }
    });

    ws.on('close', () => {
      if (ws.userId) {
        wsConnections.delete(ws.userId);
        console.log(`User ${ws.userId} disconnected from WebSocket`);
      }
    });

    ws.on('error', (error: any) => {
      console.error('WebSocket error:', error);
    });
  });

  return httpServer;
}

