      requestedTier: data.requestedTier,
      reason: data.reason,
      status: 'pending',
      createdAt: new Date(),
      updatedAt: new Date()
    };

    // Store in memory for now (in real app, save to database)
    this.tierUpgradeRequests.push(request);

    return request;
