    baseUrl: 'https://api-seller.ozon.ru',
    documentationUrl: 'https://docs.ozon.ru/api/seller/en/',
    apiVersion: 'v3',
    endpoints: ['/v3/product', '/v3/order', '/v3/analytics'],
    authentication: 'Client ID + Secret',
    rateLimits: '100 requests/second'
  }
};

interface ApiDocumentationManagerProps {
  partnerId?: string;
  isOpen: boolean;
  onClose: () => void;
  onSuccess?: () => void;
}

export function ApiDocumentationManager({ 
  partnerId, 
  isOpen, 
  onClose, 
  onSuccess 
}: ApiDocumentationManagerProps) {
  const { toast } = useToast();
  const [documentations, setDocumentations] = useState<ApiDocumentation[]>([]);
  const [selectedMarketplace, setSelectedMarketplace] = useState<string>('');
  const [apiDocUrl, setApiDocUrl] = useState<string>('');
  const [shopId, setShopId] = useState<string>('');
  const [notes, setNotes] = useState<string>('');
  const [isLoading, setIsLoading] = useState(false);
  const [isVerifying, setIsVerifying] = useState<string | null>(null);

  useEffect(() => {
    if (isOpen && partnerId) {
      loadDocumentations();
    }
  }, [isOpen, partnerId]);

  const loadDocumentations = async () => {
    try {
      const response = await apiRequest('GET', `/api/partners/${partnerId}/api-documentations`);
      const data = await response.json();
      setDocumentations(data);
    } catch (error) {
      console.error('Failed to load API documentations:', error);
    }
  };

  const handleAddDocumentation = async () => {
    if (!selectedMarketplace || !apiDocUrl) {
      toast({
        title: "Ma'lumotlar to'liq emas",
        description: "Marketplace va API hujjat URL manzilini kiriting",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);
    try {
      const response = await apiRequest('POST', `/api/partners/${partnerId}/api-documentations`, {
        marketplace: selectedMarketplace,
        apiDocumentationUrl: apiDocUrl,
        shopId: shopId || undefined,
        notes: notes || undefined
      });

      const newDoc = await response.json();
      setDocumentations(prev => [...prev, newDoc]);

      toast({
        title: "API hujjat qo'shildi",
        description: `${MARKETPLACE_API_INFO[selectedMarketplace as keyof typeof MARKETPLACE_API_INFO]?.name || selectedMarketplace} uchun API hujjat muvaffaqiyatli qo'shildi`,
      });

      // Reset form
      setSelectedMarketplace('');
      setApiDocUrl('');
      setShopId('');
      setNotes('');

      onSuccess?.();
    } catch (error: any) {
      toast({
        title: "Xatolik",
        description: error.message || "API hujjat qo'shishda xatolik yuz berdi",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleVerifyDocumentation = async (docId: string) => {
    setIsVerifying(docId);
    try {
      const response = await apiRequest('POST', `/api/api-documentations/${docId}/verify`);
      const result = await response.json();

      if (result.verified) {
        toast({
          title: "Hujjat tasdiqlandi",
          description: "API hujjat muvaffaqiyatli tasdiqlandi",
        });
      } else {
        toast({
          title: "Tasdiqlash muvaffaqiyatsiz",
          description: result.error || "API hujjatni tasdiqlashda xatolik",
          variant: "destructive",
        });
      }

      // Refresh list
      loadDocumentations();
    } catch (error: any) {
      toast({
        title: "Tasdiqlash xatoligi",
        description: error.message || "API hujjatni tasdiqlashda xatolik",
        variant: "destructive",
      });
    } finally {
      setIsVerifying(null);
    }
  };

  const handleDeleteDocumentation = async (docId: string) => {
    if (!confirm("Bu API hujjatni o'chirishni xohlaysizmi?")) return;

    try {
      await apiRequest('DELETE', `/api/api-documentations/${docId}`);
      setDocumentations(prev => prev.filter(doc => doc.id !== docId));

      toast({
        title: "API hujjat o'chirildi",
        description: "API hujjat muvaffaqiyatli o'chirildi",
      });
    } catch (error: any) {
      toast({
        title: "O'chirish xatoligi",
        description: error.message || "API hujjatni o'chirishda xatolik",
        variant: "destructive",
      });
    }
  };

  const getStatusBadge = (status: string) => {
    const config = {
      active: { label: 'Faol', variant: 'default' as const, icon: CheckCircle },
      pending: { label: 'Kutilmoqda', variant: 'secondary' as const, icon: AlertTriangle },
      error: { label: 'Xatolik', variant: 'destructive' as const, icon: XCircle }
    };

    const statusConfig = config[status as keyof typeof config] || config.pending;
    const Icon = statusConfig.icon;

    return (
      <Badge variant={statusConfig.variant} className="flex items-center gap-1">
        <Icon className="w-3 h-3" />
        {statusConfig.label}
      </Badge>
    );
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-6xl max-h-[90vh] overflow-y-auto">
        <div className="p-6 border-b">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-bold text-slate-900">API Hujjatlar Boshqaruvi</h2>
              <p className="text-slate-600">Hamkor marketplace API hujjatlarini boshqarish</p>
            </div>
            <Button variant="outline" onClick={onClose}>
              <XCircle className="w-4 h-4 mr-2" />
              Yopish
            </Button>
          </div>
        </div>

        <div className="p-6 space-y-6">
          {/* Add New Documentation Form */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Plus className="w-5 h-5" />
                Yangi API Hujjat Qo'shish
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="marketplace">Marketplace</Label>
                  <Select value={selectedMarketplace} onValueChange={setSelectedMarketplace}>
                    <SelectTrigger>
                      <SelectValue placeholder="Marketplace tanlang" />
                    </SelectTrigger>
                    <SelectContent>
                      {Object.entries(MARKETPLACE_API_INFO).map(([key, info]) => (
                        <SelectItem key={key} value={key}>
                          <div className="flex items-center gap-2">
                            <Globe className="w-4 h-4" />
                            {info.name}
                          </div>
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="shopId">Do'kon ID (ixtiyoriy)</Label>
                  <Input
                    id="shopId"
                    value={shopId}
                    onChange={(e) => setShopId(e.target.value)}
                    placeholder="Do'kon identifikatori"
                  />
                </div>

                <div className="md:col-span-2">
                  <Label htmlFor="apiDocUrl">API Hujjat URL</Label>
                  <Input
                    id="apiDocUrl"
                    value={apiDocUrl}
                    onChange={(e) => setApiDocUrl(e.target.value)}
                    placeholder="https://api-seller.uzum.uz/api/seller-openapi/swagger/..."
                  />
                  {selectedMarketplace && MARKETPLACE_API_INFO[selectedMarketplace] && (
                    <div className="mt-2 p-3 bg-blue-50 rounded-lg">
                      <div className="flex items-center gap-2 mb-2">
                        <BookOpen className="w-4 h-4 text-blue-600" />
                        <span className="text-sm font-medium text-blue-800">
                          {MARKETPLACE_API_INFO[selectedMarketplace].name} API Ma'lumotlari
                        </span>
                      </div>
                      <div className="grid grid-cols-1 md:grid-cols-3 gap-2 text-xs text-blue-700">
                        <div>Base URL: {MARKETPLACE_API_INFO[selectedMarketplace].baseUrl}</div>
                        <div>API Version: {MARKETPLACE_API_INFO[selectedMarketplace].apiVersion}</div>
                        <div>Auth: {MARKETPLACE_API_INFO[selectedMarketplace].authentication}</div>
                      </div>
                      <div className="mt-2">
                        <a 
                          href={MARKETPLACE_API_INFO[selectedMarketplace as keyof typeof MARKETPLACE_API_INFO]?.documentationUrl || '#'}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-blue-600 hover:text-blue-800 text-xs flex items-center gap-1"
                        >
                          <ExternalLink className="w-3 h-3" />
                          Rasmiy hujjatlarni ko'rish
                        </a>
                      </div>
                    </div>
                  )}
                </div>

                <div className="md:col-span-2">
                  <Label htmlFor="notes">Izohlar (ixtiyoriy)</Label>
                  <Textarea
                    id="notes"
                    value={notes}
                    onChange={(e) => setNotes(e.target.value)}
                    placeholder="API hujjat haqida qo'shimcha ma'lumotlar..."
                    rows={3}
                  />
                </div>

                <div className="md:col-span-2">
                  <Button 
                    onClick={handleAddDocumentation}
                    disabled={isLoading || !selectedMarketplace || !apiDocUrl}
                    className="w-full"
                  >
                    {isLoading ? (
                      <>
                        <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                        Qo'shilmoqda...
                      </>
                    ) : (
                      <>
                        <Plus className="w-4 h-4 mr-2" />
                        API Hujjat Qo'shish
                      </>
                    )}
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Existing Documentations */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <FileText className="w-5 h-5" />
                Mavjud API Hujjatlar
              </CardTitle>
            </CardHeader>
            <CardContent>
              {documentations.length === 0 ? (
                <div className="text-center py-8 text-slate-500">
                  <FileText className="w-12 h-12 mx-auto mb-4 text-slate-300" />
                  <p>Hozircha API hujjatlar yo'q</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {documentations.map((doc) => (
                    <div key={doc.id} className="border rounded-lg p-4">
                      <div className="flex items-center justify-between mb-3">
                        <div className="flex items-center gap-3">
                          <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
                            <Globe className="w-5 h-5 text-blue-600" />
                          </div>
                          <div>
                            <h4 className="font-semibold text-slate-900">
                              {MARKETPLACE_API_INFO[doc.marketplace as keyof typeof MARKETPLACE_API_INFO]?.name || doc.marketplace}
                            </h4>
                            <p className="text-sm text-slate-600">
                              {doc.shopId ? `Do'kon ID: ${doc.shopId}` : "Do'kon ID kiritilmagan"}
                            </p>
                          </div>
                        </div>
                        <div className="flex items-center gap-2">
                          {getStatusBadge(doc.status)}
                          <div className="flex gap-1">
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => handleVerifyDocumentation(doc.id)}
                              disabled={isVerifying === doc.id}
                            >
                              {isVerifying === doc.id ? (
                                <RefreshCw className="w-3 h-3 animate-spin" />
                              ) : (
                                <CheckCircle className="w-3 h-3" />
                              )}
                            </Button>
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => window.open(doc.apiDocumentationUrl, '_blank')}
                            >
                              <ExternalLink className="w-3 h-3" />
                            </Button>
                            <Button
                              size="sm"
                              variant="destructive"
                              onClick={() => handleDeleteDocumentation(doc.id)}
                            >
                              <Trash2 className="w-3 h-3" />
                            </Button>
                          </div>
                        </div>
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                        <div>
                          <p className="text-slate-500">API URL:</p>
                          <p className="font-mono text-xs break-all">{doc.apiDocumentationUrl}</p>
                        </div>
                        <div>
                          <p className="text-slate-500">So'nggi tekshiruv:</p>
                          <p>{new Date(doc.lastVerified).toLocaleDateString('uz-UZ')}</p>
                        </div>
                        <div>
                          <p className="text-slate-500">Tasdiqlash holati:</p>
                          <Badge variant={doc.verificationStatus === 'verified' ? 'default' : 'secondary'}>
                            {doc.verificationStatus === 'verified' ? 'Tasdiqlangan' : 'Tasdiqlanmagan'}
                          </Badge>
                        </div>
                      </div>

                      {doc.notes && (
                        <div className="mt-3 p-3 bg-slate-50 rounded-lg">
                          <p className="text-sm text-slate-600">{doc.notes}</p>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>

          {/* Marketplace API Information */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Database className="w-5 h-5" />
                Marketplace API Ma'lumotlari
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {Object.entries(MARKETPLACE_API_INFO).map(([key, info]) => (
                  <div key={key} className="border rounded-lg p-4">
                    <div className="flex items-center gap-2 mb-3">
                      <div className="w-8 h-8 bg-blue-500 rounded flex items-center justify-center">
                        <span className="text-white text-sm font-bold">
                          {info.name.charAt(0)}
                        </span>
                      </div>
                      <h4 className="font-semibold">{info.name}</h4>
                    </div>

                    <div className="space-y-2 text-sm">
                      <div className="flex justify-between">
                        <span className="text-slate-500">Base URL:</span>
                        <span className="font-mono text-xs">{info.baseUrl}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-slate-500">API Version:</span>
                        <span>{info.apiVersion}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-slate-500">Autentifikatsiya:</span>
                        <span>{info.authentication}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-slate-500">Cheklovlar:</span>
                        <span>{info.rateLimits}</span>
                      </div>
                    </div>

                    <div className="mt-3 pt-3 border-t">
                      <p className="text-xs text-slate-500 mb-2">Mavjud endpointlar:</p>
                      <div className="flex flex-wrap gap-1">
                        {info.endpoints.map((endpoint, index) => (
                          <Badge key={index} variant="outline" className="text-xs">
                            {endpoint}
                          </Badge>
                        ))}
                      </div>
                    </div>

                    <div className="mt-3">
                      <a
                        href={info.documentationUrl}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-600 hover:text-blue-800 text-xs flex items-center gap-1"
                      >
                        <ExternalLink className="w-3 h-3" />
                        Rasmiy hujjatlarni ko'rish
                      </a>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/components/ApiKeyForm.tsx.content:
import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import { X, Globe, Key, Eye, EyeOff } from 'lucide-react';

interface ApiKeyFormProps {
  isOpen: boolean;
  onClose: () => void;
  partnerId: string;
  marketplace: string;
}

export function ApiKeyForm({ isOpen, onClose, partnerId, marketplace }: ApiKeyFormProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [formData, setFormData] = useState({
    apiKey: '',
    apiSecret: '',
    shopId: '',
  });
  const [showSecrets, setShowSecrets] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const connectMarketplaceMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest('POST', `/api/partners/${partnerId}/marketplace/connect`, data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Marketplace ulandi",
        description: `${marketplace} muvaffaqiyatli ulandi`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/marketplace-integrations'] });
      onClose();
      setFormData({ apiKey: '', apiSecret: '', shopId: '' });
    },
    onError: (error: Error) => {
      toast({
        title: "Ulanish xatoligi",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      await connectMarketplaceMutation.mutateAsync({
        marketplace: (marketplace || 'uzum').toLowerCase(),
        ...formData
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const getMarketplaceInfo = () => {
    const info = {
      uzum: {
        name: 'Uzum Market',
        color: 'bg-blue-500',
        icon: 'U',
        description: 'Uzbekistan\'s leading marketplace'
      },
      wildberries: {
        name: 'Wildberries',
        color: 'bg-purple-500',
        icon: 'W',
        description: 'International fashion marketplace'
      },
      yandex: {
        name: 'Yandex Market',
        color: 'bg-red-500',
        icon: 'Y',
        description: 'Russian e-commerce platform'
      },
      ozon: {
        name: 'Ozon',
        color: 'bg-orange-500',
        icon: 'O',
        description: 'Russian online retailer'
      }
    };
    return info[(marketplace?.toLowerCase() || 'uzum') as keyof typeof info] || info.uzum;
  };

  const marketplaceInfo = getMarketplaceInfo();

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <div className={`w-8 h-8 ${marketplaceInfo.color} rounded flex items-center justify-center`}>
              <span className="text-white font-bold">{marketplaceInfo.icon}</span>
            </div>
            {marketplaceInfo.name} ulash
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="apiKey">API Key</Label>
            <div className="relative">
              <Input
                id="apiKey"
                type={showSecrets ? 'text' : 'password'}
                value={formData.apiKey}
                onChange={(e) => handleInputChange('apiKey', e.target.value)}
                placeholder="API Key kiriting"
                required
              />
              <Button
                type="button"
                variant="ghost"
                size="sm"
                className="absolute right-2 top-1/2 transform -translate-y-1/2 h-auto p-1"
                onClick={() => setShowSecrets(!showSecrets)}
              >
                {showSecrets ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
              </Button>
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="apiSecret">API Secret</Label>
            <div className="relative">
              <Input
                id="apiSecret"
                type={showSecrets ? 'text' : 'password'}
                value={formData.apiSecret}
                onChange={(e) => handleInputChange('apiSecret', e.target.value)}
                placeholder="API Secret kiriting"
                required
              />
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="shopId">Shop ID</Label>
            <Input
              id="shopId"
              type="text"
              value={formData.shopId}
              onChange={(e) => handleInputChange('shopId', e.target.value)}
              placeholder="Shop ID kiriting"
            />
          </div>

          <Alert>
            <Globe className="h-4 w-4" />
            <AlertDescription>
              {marketplaceInfo.description}. API ma'lumotlarini {marketplaceInfo.name} admin panelidan olishingiz mumkin.
            </AlertDescription>
          </Alert>

          <div className="flex gap-2 pt-4">
            <Button
              type="button"
              variant="outline"
              onClick={onClose}
              className="flex-1"
            >
              Bekor qilish
            </Button>
            <Button
              type="submit"
              disabled={isLoading || !formData.apiKey || !formData.apiSecret}
              className="flex-1"
            >
              {isLoading ? 'Ulanmoqda...' : 'Ulash'}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ExcelDataManager.t.content:
import React, { useState, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { 
  FileSpreadsheet, 
  Upload, 
  Download, 
  CheckCircle, 
  XCircle, 
  AlertTriangle,
  RefreshCw,
  Eye,
  Trash2,
  Plus,
  Database,
  BarChart3,
  TrendingUp,
  ShoppingCart,
  Package,
  Calendar,
  FileText,
  Settings,
  Globe,
  Users,
  DollarSign
} from 'lucide-react';

interface ExcelImport {
  id: string;
  partnerId: string;
  marketplace: string;
  fileName: string;
  fileSize: number;
  importType: 'sales_data' | 'product_list' | 'inventory';
  status: 'processing' | 'completed' | 'failed';
  recordsProcessed: number;
  recordsTotal: number;
  errorCount: number;
  successCount: number;
  errorDetails: any[];
  processedAt: string;
  createdAt: string;
}

interface ExcelTemplate {
  id: string;
  name: string;
  description: string;
  marketplace: string;
  templateType: 'sales_report' | 'product_catalog' | 'inventory';
  columns: string[];
  isActive: boolean;
}

interface ExcelDataManagerProps {
  partnerId?: string;
  isOpen: boolean;
  onClose: () => void;
  onSuccess?: () => void;
}

const IMPORT_TYPES = {
  sales_data: {
    label: 'Savdo Ma\'lumotlari',
    description: 'Buyurtmalar, to\'lovlar va savdo statistikasi',
    icon: ShoppingCart,
    columns: ['Sana', 'Buyurtma ID', 'Mahsulot', 'Miqdor', 'Narx', 'To\'lov turi', 'Status']
  },
  product_list: {
    label: 'Mahsulotlar Ro\'yxati',
    description: 'Mahsulotlar katalogi va narxlari',
    icon: Package,
    columns: ['Mahsulot ID', 'Nomi', 'Kategoriya', 'Narx', 'Sklon', 'Status']
  },
  inventory: {
    label: 'Sklon Ma\'lumotlari',
    description: 'Sklon qoldiqlari va harakatlar',
    icon: Database,
    columns: ['Mahsulot ID', 'Nomi', 'Sklon', 'Kelgan', 'Ketgan', 'Qoldiq']
  }
};

const MARKETPLACES = {
  uzum: { name: 'Uzum Market', color: 'bg-blue-500' },
  wildberries: { name: 'Wildberries', color: 'bg-purple-500' },
  yandex: { name: 'Yandex Market', color: 'bg-red-500' },
  ozon: { name: 'Ozon', color: 'bg-orange-500' }
};

export function ExcelDataManager({ 
  partnerId, 
  isOpen, 
  onClose, 
  onSuccess 
}: ExcelDataManagerProps) {
  const { toast } = useToast();
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [imports, setImports] = useState<ExcelImport[]>([]);
  const [templates, setTemplates] = useState<ExcelTemplate[]>([]);
  const [selectedImportType, setSelectedImportType] = useState<string>('');
  const [selectedMarketplace, setSelectedMarketplace] = useState<string>('');
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [isUploading, setIsUploading] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      // Check file type
      if (!file.name.endsWith('.xlsx') && !file.name.endsWith('.xls') && !file.name.endsWith('.csv')) {
        toast({
          title: "Noto'g'ri fayl turi",
          description: "Faqat Excel (.xlsx, .xls) yoki CSV fayllarini yuklashingiz mumkin",
          variant: "destructive",
        });
        return;
      }

      // Check file size (max 10MB)
      if (file.size > 10 * 1024 * 1024) {
        toast({
          title: "Fayl hajmi juda katta",
          description: "Fayl hajmi 10MB dan katta bo'lishi mumkin emas",
          variant: "destructive",
        });
        return;
      }

      setSelectedFile(file);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile || !selectedImportType || !selectedMarketplace) {
      toast({
        title: "Ma'lumotlar to'liq emas",
        description: "Fayl, import turi va marketplace tanlang",
        variant: "destructive",
      });
      return;
    }

    setIsUploading(true);
    const formData = new FormData();
    formData.append('file', selectedFile);
    formData.append('importType', selectedImportType);
    formData.append('marketplace', selectedMarketplace);

    try {
      const response = await apiRequest('POST', `/api/partners/${partnerId}/excel-import`, formData, {
        headers: {
          // Don't set Content-Type, let browser set it with boundary
        }
      });

      const result = await response.json();

      toast({
        title: "Fayl yuklandi",
        description: "Excel fayl muvaffaqiyatli yuklandi va qayta ishlanmoqda",
      });

      // Reset form
      setSelectedFile(null);
      setSelectedImportType('');
      setSelectedMarketplace('');
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }

      // Refresh imports list
      loadImports();
      onSuccess?.();
    } catch (error: any) {
      toast({
        title: "Yuklash xatoligi",
        description: error.message || "Faylni yuklashda xatolik yuz berdi",
        variant: "destructive",
      });
    } finally {
      setIsUploading(false);
    }
  };

  const loadImports = async () => {
    try {
      const response = await apiRequest('GET', `/api/partners/${partnerId}/excel-imports`);
      const data = await response.json();
      setImports(data);
    } catch (error) {
      console.error('Failed to load imports:', error);
    }
  };

  const loadTemplates = async () => {
    try {
      const response = await apiRequest('GET', '/api/excel-templates');
      const data = await response.json();
      setTemplates(data);
    } catch (error) {
      console.error('Failed to load templates:', error);
    }
  };

  const handleDownloadTemplate = async (templateId: string) => {
    try {
      const response = await apiRequest('GET', `/api/excel-templates/${templateId}/download`);
      const blob = await response.blob();

      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `template_${templateId}.xlsx`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

      toast({
        title: "Shablon yuklandi",
        description: "Excel shablon muvaffaqiyatli yuklandi",
      });
    } catch (error: any) {
      toast({
        title: "Yuklash xatoligi",
        description: error.message || "Shablonni yuklashda xatolik",
        variant: "destructive",
      });
    }
  };

  const handleExportData = async (marketplace: string, dataType: string) => {
    try {
      const response = await apiRequest('GET', `/api/partners/${partnerId}/export/${marketplace}/${dataType}`);
      const blob = await response.blob();

      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${marketplace}_${dataType}_${new Date().toISOString().split('T')[0]}.xlsx`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

      toast({
        title: "Ma'lumotlar eksport qilindi",
        description: `${MARKETPLACES[marketplace as keyof typeof MARKETPLACES]?.name || marketplace} ma'lumotlari yuklandi`,
      });
    } catch (error: any) {
      toast({
        title: "Eksport xatoligi",
        description: error.message || "Ma'lumotlarni eksport qilishda xatolik",
        variant: "destructive",
      });
    }
  };

  const getStatusBadge = (status: string) => {
    const config = {
      processing: { label: 'Qayta ishlanmoqda', variant: 'secondary' as const, icon: RefreshCw },
      completed: { label: 'Yakunlandi', variant: 'default' as const, icon: CheckCircle },
      failed: { label: 'Xatolik', variant: 'destructive' as const, icon: XCircle }
    };

    const statusConfig = config[status as keyof typeof config] || config.processing;
    const Icon = statusConfig.icon;

    return (
      <Badge variant={statusConfig.variant} className="flex items-center gap-1">
        <Icon className="w-3 h-3" />
        {statusConfig.label}
      </Badge>
    );
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-6xl max-h-[90vh] overflow-y-auto">
        <div className="p-6 border-b">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-bold text-slate-900">Excel Ma'lumotlar Boshqaruvi</h2>
              <p className="text-slate-600">Marketplace ma'lumotlarini Excel orqali import/export qilish</p>
            </div>
            <Button variant="outline" onClick={onClose}>
              <XCircle className="w-4 h-4 mr-2" />
              Yopish
            </Button>
          </div>
        </div>

        <div className="p-6 space-y-6">
          {/* Upload Section */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Upload className="w-5 h-5" />
                Excel Fayl Yuklash
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <Label htmlFor="marketplace">Marketplace</Label>
                  <Select value={selectedMarketplace} onValueChange={setSelectedMarketplace}>
                    <SelectTrigger>
                      <SelectValue placeholder="Marketplace tanlang" />
                    </SelectTrigger>
                    <SelectContent>
                      {Object.entries(MARKETPLACES).map(([key, info]) => (
                        <SelectItem key={key} value={key}>
                          <div className="flex items-center gap-2">
                            <div className={`w-3 h-3 rounded-full ${info.color}`}></div>
                            {info.name}
                          </div>
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="importType">Import Turi</Label>
                  <Select value={selectedImportType} onValueChange={setSelectedImportType}>
                    <SelectTrigger>
                      <SelectValue placeholder="Import turini tanlang" />
                    </SelectTrigger>
                    <SelectContent>
                      {Object.entries(IMPORT_TYPES).map(([key, info]) => {
                        const Icon = info.icon;
                        return (
                          <SelectItem key={key} value={key}>
                            <div className="flex items-center gap-2">
                              <Icon className="w-4 h-4" />
                              {info.label}
                            </div>
                          </SelectItem>
                        );
                      })}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="file">Fayl Tanlash</Label>
                  <Input
                    ref={fileInputRef}
                    type="file"
                    accept=".xlsx,.xls,.csv"
                    onChange={handleFileSelect}
                    className="cursor-pointer"
                  />
                </div>
              </div>

              {selectedFile && (
                <div className="mt-4 p-4 bg-blue-50 rounded-lg">
                  <div className="flex items-center gap-3">
                    <FileSpreadsheet className="w-8 h-8 text-blue-600" />
                    <div>
                      <h4 className="font-semibold text-blue-900">{selectedFile.name}</h4>
                      <p className="text-sm text-blue-700">
                        Hajm: {formatFileSize(selectedFile.size)} | 
                        Tur: {selectedFile.type || 'Excel fayl'}
                      </p>
                    </div>
                  </div>
                </div>
              )}

              {selectedImportType && IMPORT_TYPES[selectedImportType as keyof typeof IMPORT_TYPES] && (
                <div className="mt-4 p-4 bg-green-50 rounded-lg">
                  <div className="flex items-center gap-2 mb-2">
                    <Info className="w-4 h-4 text-green-600" />
                    <span className="text-sm font-medium text-green-800">
                      {IMPORT_TYPES[selectedImportType as keyof typeof IMPORT_TYPES]?.label || 'Import Type'}
                    </span>
                  </div>
                  <p className="text-sm text-green-700 mb-2">
                    {IMPORT_TYPES[selectedImportType as keyof typeof IMPORT_TYPES]?.description || ''}
                  </p>
                  <div className="text-xs text-green-600">
                    <p className="font-medium">Kerakli ustunlar:</p>
                    <div className="flex flex-wrap gap-1 mt-1">
                      {(IMPORT_TYPES[selectedImportType as keyof typeof IMPORT_TYPES]?.columns || []).map((col, index) => (
                        <Badge key={index} variant="outline" className="text-xs">
                          {col}
                        </Badge>
                      ))}
                    </div>
                  </div>
                </div>
              )}

              <div className="mt-6">
                <Button 
                  onClick={handleUpload}
                  disabled={isUploading || !selectedFile || !selectedImportType || !selectedMarketplace}
                  className="w-full"
                >
                  {isUploading ? (
                    <>
                      <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                      Yuklanmoqda...
                    </>
                  ) : (
                    <>
                      <Upload className="w-4 h-4 mr-2" />
                      Faylni Yuklash
                    </>
                  )}
                </Button>
              </div>
            </CardContent>
          </Card>

          {/* Export Section */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Download className="w-5 h-5" />
                Ma'lumotlarni Eksport Qilish
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {Object.entries(MARKETPLACES).map(([marketplace, info]) => (
                  <div key={marketplace} className="border rounded-lg p-4">
                    <div className="flex items-center gap-2 mb-3">
                      <div className={`w-8 h-8 rounded flex items-center justify-center ${info.color}`}>
                        <span className="text-white text-sm font-bold">
                          {info.name.charAt(0)}
                        </span>
                      </div>
                      <h4 className="font-semibold">{info.name}</h4>
                    </div>

                    <div className="space-y-2">
                      <Button
                        size="sm"
                        variant="outline"
                        className="w-full justify-start"
                        onClick={() => handleExportData(marketplace, 'sales')}
                      >
                        <ShoppingCart className="w-4 h-4 mr-2" />
                        Savdo Ma'lumotlari
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        className="w-full justify-start"
                        onClick={() => handleExportData(marketplace, 'products')}
                      >
                        <Package className="w-4 h-4 mr-2" />
                        Mahsulotlar
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        className="w-full justify-start"
                        onClick={() => handleExportData(marketplace, 'analytics')}
                      >
                        <BarChart3 className="w-4 h-4 mr-2" />
                        Tahlil Ma'lumotlari
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          {/* Templates Section */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <FileText className="w-5 h-5" />
                Excel Shablonlar
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {Object.entries(IMPORT_TYPES).map(([key, info]) => {
                  const Icon = info.icon;
                  return (
                    <div key={key} className="border rounded-lg p-4">
                      <div className="flex items-center gap-2 mb-3">
                        <Icon className="w-6 h-6 text-blue-600" />
                        <h4 className="font-semibold">{info.label}</h4>
                      </div>
                      <p className="text-sm text-slate-600 mb-3">{info.description}</p>
                      <Button
                        size="sm"
                        variant="outline"
                        className="w-full"
                        onClick={() => handleDownloadTemplate(key)}
                      >
                        <Download className="w-4 h-4 mr-2" />
                        Shablon Yuklash
                      </Button>
                    </div>
                  );
                })}
              </div>
            </CardContent>
          </Card>

          {/* Import History */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <History className="w-5 h-5" />
                Import Tarixi
              </CardTitle>
            </CardHeader>
            <CardContent>
              {imports.length === 0 ? (
                <div className="text-center py-8 text-slate-500">
                  <FileSpreadsheet className="w-12 h-12 mx-auto mb-4 text-slate-300" />
                  <p>Hozircha import tarixi yo'q</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {imports.slice(0, 5).map((importItem) => (
                    <div key={importItem.id} className="border rounded-lg p-4">
                      <div className="flex items-center justify-between mb-3">
                        <div className="flex items-center gap-3">
                          <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
                            <FileSpreadsheet className="w-5 h-5 text-blue-600" />
                          </div>
                          <div>
                            <h4 className="font-semibold text-slate-900">{importItem.fileName}</h4>
                            <p className="text-sm text-slate-600">
                              {MARKETPLACES[importItem.marketplace as keyof typeof MARKETPLACES]?.name || importItem.marketplace} â€¢ 
                              {IMPORT_TYPES[importItem.importType as keyof typeof IMPORT_TYPES]?.label || importItem.importType}
                            </p>
                          </div>
                        </div>
                        <div className="flex items-center gap-2">
                          {getStatusBadge(importItem.status)}
                          <span className="text-xs text-slate-500">
                            {formatFileSize(importItem.fileSize)}
                          </span>
                        </div>
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 text-sm">
                        <div>
                          <p className="text-slate-500">Qayta ishlangan:</p>
                          <p className="font-medium">{importItem.recordsProcessed} / {importItem.recordsTotal}</p>
                        </div>
                        <div>
                          <p className="text-slate-500">Muvaffaqiyatli:</p>
                          <p className="font-medium text-green-600">{importItem.successCount}</p>
                        </div>
                        <div>
                          <p className="text-slate-500">Xatolar:</p>
                          <p className="font-medium text-red-600">{importItem.errorCount}</p>
                        </div>
                        <div>
                          <p className="text-slate-500">Sana:</p>
                          <p className="font-medium">
                            {new Date(importItem.createdAt).toLocaleDateString('uz-UZ')}
                          </p>
                        </div>
                      </div>

                      {importItem.errorDetails.length > 0 && (
                        <div className="mt-3 p-3 bg-red-50 rounded-lg">
                          <p className="text-sm font-medium text-red-800 mb-2">Xatolar:</p>
                          <div className="space-y-1">
                            {importItem.errorDetails.slice(0, 3).map((error, index) => (
                              <p key={index} className="text-xs text-red-700">
                                {error.message}
                              </p>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

